import React, { useState, useRef, useEffect } from "react";
import img from "../../components/img/image 9.png";
import img1 from "../../components/img/Screen Shot 2023-06-28 at 1.41.31 PM.png";
import { Link } from "react-router-dom";
import "./search.css";

export default function Search() {
  const [searchText, setSearchText] = useState("");
  const [showFacetMenu, setShowFacetMenu] = useState(false);
  const popupRef = useRef(null);

  const handleSearch = () => {
    console.log("Search Address:", searchText);
  };

  const toggleFacetMenu = () => {
    setShowFacetMenu(!showFacetMenu);
  };

  const handleClickOutside = (event) => {
    if (popupRef.current && !popupRef.current.contains(event.target)) {
      setShowFacetMenu(false);
    }
  };

  useEffect(() => {
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <>
      <div className="topleft">
        <Link to="/">
          <img className="img" src={img} alt="Image" />
        </Link>
      </div>
      <div className="search-box">
        <input
          type="text"
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
          placeholder="Enter an address"
          className="search-input"
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      <div className="container">
        <div className="text">Explore by category</div>
        <div className="text0" onClick={toggleFacetMenu}>
          View all
        </div>
        {showFacetMenu && (
          <div className="facet-popup" ref={popupRef}>
            <div className="close-button" onClick={toggleFacetMenu}>
              X
            </div>
            <div className="facet-options">
              <input type="checkbox" id="option1" name="option1" />
              <label htmlFor="option1">Option 1</label>
            </div>
            <div className="facet-options">
              <input type="checkbox" id="option2" name="option2" />
              <label htmlFor="option2">Option 2</label>
            </div>
            <div className="facet-options">
              <input type="checkbox" id="option3" name="option3" />
              <label htmlFor="option3">Option 3</label>
            </div>
            {/* Add more checkbox options as needed */}
          </div>
        )}
      </div>
      <div className="bottom">
        <Link to="/">
          <img className="img0" src={img1} alt="Image" />
        </Link>
      </div>
    </>
  );
}
