import { useState } from "react";
import { Link } from "react-router-dom";
import "./topbar.css";
import logo from "../../components/img/logo.png";

export default function TopBar() {
  const [isContinentDropdownOpen, setIsContinentDropdownOpen] = useState(false);
  const [isCountryDropdownOpen, setIsCountryDropdownOpen] = useState(false);

  const handleContinentDropdownToggle = () => {
    setIsContinentDropdownOpen(!isContinentDropdownOpen);
  };

  const handleCountryDropdownToggle = () => {
    setIsCountryDropdownOpen(!isCountryDropdownOpen);
  };

  return (
    <div className="top">
      <div className="topleft">
        <img className="logo" src={logo} alt="react logo" />
      </div>
      <div className="topcenter">
        <ul className="toplist">
          <li className="toplistitem">
            <Link className="link" to="/"></Link>
          </li>
        </ul>
      </div>

      <div className="topright">
        <ul className="toplist">
          <li
            className={`toplistitem ${isContinentDropdownOpen ? "active" : ""}`}
            onClick={handleContinentDropdownToggle}
          >
            <span className="dropdown-trigger">Continents</span>
            <i className={`dropdown-icon fas ${isContinentDropdownOpen ? "fa-caret-down" : "fa-caret-up"}`}></i>
            {isContinentDropdownOpen && (
              <ul className="dropdown-menu">
                <li
                  className={`dropdown-item ${isCountryDropdownOpen ? "active" : ""}`}
                  onClick={handleCountryDropdownToggle}
                >
                  <span className="dropdown-trigger">Asia</span>
                  <i className={`dropdown-icon fas ${isCountryDropdownOpen ? "fa-caret-down" : "fa-caret-up"}`}></i>
                  {isCountryDropdownOpen && (
                    <ul className="dropdown-menu">
                      <li>
                        <Link className="dropdown-link" to="/country1">
                          Country 1
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-link" to="/country2">
                          Country 2
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-link" to="/country3">
                          Country 3
                        </Link>
                      </li>
                    </ul>
                  )}
                </li>
                <li>
                  <Link className="dropdown-link" to="/continent2">
                    Continent 2
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-link" to="/continent3">
                    Continent 3
                  </Link>
                </li>
              </ul>
            )}
          </li>
        </ul>
        <div className="button-wrapper">
          <button className="login-button">Log In</button>
          <button className="signup-button">Sign Up</button>
        </div>
      </div>
    </div>
  );
}
