import { useState } from "react";
import { Link } from "react-router-dom";
import "./topbar.css";
import logo from "../../components/img/logo.png";

export default function TopBar() {
  const [isContinentDropdownOpen, setIsContinentDropdownOpen] = useState(false);
  const [isCountryDropdownOpen, setIsCountryDropdownOpen] = useState(false);

  const handleContinentDropdownToggle = () => {
    setIsContinentDropdownOpen(!isContinentDropdownOpen);
  };

  const handleCountryDropdownToggle = () => {
    setIsCountryDropdownOpen(!isCountryDropdownOpen);
  };

  return (
    <div className="top">
      <div className="topleft">
        <Link to="/">
          <img className="logo" src={logo} alt="react logo" />
        </Link>
      </div>
      <div className="topcenter">
        <ul className="toplist">
          <li className="toplistitem">
            <Link className="link" to="/">
              Home
            </Link>
          </li>
          <li
            className={`toplistitem dropdown-item ${
              isContinentDropdownOpen ? "active" : ""
            }`}
            onClick={handleContinentDropdownToggle}
          >
            Where do we operate
            <i
              className={`dropdown-icon fas ${
                isContinentDropdownOpen ? "fa-caret-up" : "fa-caret-down"
              }`}
            ></i>
            {isContinentDropdownOpen && (
              <ul className="dropdown-menu">
                <li
                  className={`dropdown-item ${
                    isCountryDropdownOpen ? "active" : ""
                  }`}
                  onClick={handleCountryDropdownToggle}
                >
                  <span className="dropdown-trigger">Africa</span>
                  <i
                    className={`dropdown-icon fas ${
                      isCountryDropdownOpen ? "fa-caret-up" : "fa-caret-down"
                    }`}
                  ></i>
                  {isCountryDropdownOpen && (
                    <ul className="dropdown-menu">
                      <li>
                        <Link className="dropdown-link" to="/morocco">
                          Morocco
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-link" to="/egypt">
                          Egypt
                        </Link>
                      </li>
                      <li>
                        <Link className="dropdown-link" to="/southafrica">
                          South Africa
                        </Link>
                      </li>
                    </ul>
                  )}
                </li>
                <li>
                  <Link className="dropdown-link" to="/location2">
                    Europe
                  </Link>
                </li>
                <li>
                  <Link className="dropdown-link" to="/location3">
                    Americas
                  </Link>
                </li>
              </ul>
            )}
          </li>
        </ul>
      </div>

      <div className="topright">
        <ul className="toplist">
        </ul>
        <div className="button-wrapper">
          <button className="login-button">Log In</button>
          <button className="signup-button">Sign Up</button>
        </div>
      </div>
    </div>
  );
}
