export default function TopBar() {
  const [isContinentDropdownOpen, setIsContinentDropdownOpen] = useState(false);
  const [selectedContinent, setSelectedContinent] = useState(null);
  const [isCountryDropdownOpen, setIsCountryDropdownOpen] = useState(false);

  const handleContinentDropdownToggle = () => {
    setIsContinentDropdownOpen(!isContinentDropdownOpen);
  };

  const handleContinentSelection = (continent) => {
    setSelectedContinent(continent);
    setIsCountryDropdownOpen(true);
  };

  const handleCountryDropdownToggle = () => {
    setIsCountryDropdownOpen(!isCountryDropdownOpen);
  };

  return (
    <div className="top">
      {/* Existing code */}

      <div className="topcenter">
        <ul className="toplist">
          <li
            className={`toplistitem dropdown-item ${
              isContinentDropdownOpen ? "active" : ""
            }`}
            onClick={handleContinentDropdownToggle}
          >
            Where we operate
            <i
              className={`dropdown-icon fas ${
                isContinentDropdownOpen ? "fa-caret-up" : "fa-caret-down"
              }`}
            ></i>
            {isContinentDropdownOpen && (
              <ul className="dropdown-menu continent-dropdown">
                <li>
                  <span
                    className="dropdown-link"
                    onClick={() => handleContinentSelection("Africa")}
                  >
                    Africa
                  </span>
                </li>
                <li>
                  <span
                    className="dropdown-link"
                    onClick={() => handleContinentSelection("Europe")}
                  >
                    Europe
                  </span>
                </li>
                <li>
                  <span
                    className="dropdown-link"
                    onClick={() => handleContinentSelection("Americas")}
                  >
                    Americas
                  </span>
                </li>
              </ul>
            )}
          </li>
        </ul>
        {isCountryDropdownOpen && (
          <ul className="dropdown-menu country-dropdown">
            {/* Render country dropdown based on selectedContinent */}
            {selectedContinent === "Africa" && (
              <li>
                <Link className="dropdown-link" to="/country1">
                  Morocco
                </Link>
              </li>
              // Add other countries for Africa here
            )}
            {selectedContinent === "Europe" && (
              <li>
                <Link className="dropdown-link" to="/country4">
                  Country 4
                </Link>
              </li>
              // Add other countries for Europe here
            )}
            {selectedContinent === "Americas" && (
              <li>
                <Link className="dropdown-link" to="/country7">
                  Country 7
                </Link>
              </li>
              // Add other countries for Americas here
            )}
          </ul>
        )}
      </div>

      <div className="topright">
        <ul className="toplist">
        </ul>
        <div className="button-wrapper">
          <button className="login-button">Log In</button>
          <button className="signup-button">Sign Up</button>
        </div>
      </div>
    </div>
  );
}
